import React , {Component} from 'react';
import Child from './test';
import Items from "./component/items";

class App extends Component{

  change = () =>{
    this.setState ({
      name : "ay 7aga b2aaaaaa"
    })
  }
  state = {
    name : ""
  }
  changing = (e) =>{
    this.setState({
      name : e.target.value 
    })
  }

  submiting = (e) =>{
    e.preventDefault();
    this.setState({
      name : e.target.value
    })
  }



render(){
    return (
      <div className="app">
        <Child/>

        <button onClick={this.change}>click to change</button>
        <p>{ this.state.name }</p>
        <p>{this.state.age}</p>


        <br></br>

        <form onSubmit={this.submiting} >
          <input type="text" onSubmit={this.changing} ></input>
          <button>Submit</button>
        </form>

      </div>
    );
  }
}
export default App;
