    this.setState ({
      name : "ay 7aga b2aaaaaa"
    })
  }

  state = {
    name : ""
  }

  changing = (e) =>{
    this.setState({
      name : e.target.value 
    })
  }

  submiting = (e) =>{
    e.preventDefault();
    this.setState({
      name : e.target.value
    })
  }



render(){
    return (
      <div className="app">
        <Child/>



        <form onSubmit={this.submiting} >
          <input type="text" onChange={this.changing} ></input>
          <button>Submit</button>
        </form>

      </div>
    );
  }
}
export default App;
