import React , {Component} from 'react';
import Child from './test';
import Items from "./component/items";

class App extends Component{
  state = {
    item: [
      {id:1 , name:"ahmed" , age:20},
      {id:2 , name:"mohamed" , age:26}
    ]
  }

  change = () =>{
    this.setState ({
      name : "ay 7aga b2aaaaaa"
    })
  }
  state = {
    name : ""
  }
  changing = (e) =>{
    this.setState({
      name : e.target.value 
    })
  }



render(){
    return (
      <div className="app">
        <Child/>

        <button onClick={this.change}>click to change</button>
        <p>{ this.state.name }</p>
        <p>{this.state.age}</p>

        <Items  item={this.state.items}/>

        <br></br>

        <form>
          <input type="text" onChange={this.changing} ></input>
          <button>Submit</button>
        </form>
      </div>
    );
  }
}
export default App;
